<?phpif (!defined('SYS_STARTED')) die('Security activated');if (isset($_POST['password_change'])) {	$post_data = do_request($_POST, true, '');			if (!$post_data)		set_msg('Būtina užpildyti visus laukelius', 'error', $config['home_url'] . '/go/control#pc_response', 'pc_msg');			$encoded_old_password = encode_user_password($post_data['old_password']);		$query = $db->prepare("SELECT user_id FROM users WHERE user_id = '" . read_session('user_id') . "' AND password = '{$encoded_old_password}'");	$query->execute();		if ($query->rowCount() == 0)		set_msg('Neteisingas senas slaptažodis', 'error', $config['home_url'] . '/go/control#pc_response', 'pc_msg');			if (strcmp($post_data['old_password'], $post_data['new_password']) == 0)		set_msg('Senas ir naujas slaptažodis negali sutapti', 'error', $config['home_url'] . '/go/control#pc_response', 'pc_msg');			if (strcmp($post_data['new_password'], $post_data['new_password_repeat']) != 0)		set_msg('Nauji slaptažodžiai nesutampa', 'error', $config['home_url'] . '/go/control#pc_response', 'pc_msg');		$encoded_new_password = encode_user_password($post_data['new_password']);		$query = $db->prepare("UPDATE users SET password = '{$encoded_new_password}' WHERE user_id = '" . read_session('user_id') . "'");	$query->execute();		set_msg('Slaptažodis sėkmingai pakeistas', 'success', $config['home_url'] . '/go/control#pc_response', 'pc_msg');}?>